<?php

if ( ! function_exists('convertImageToBase64')) {
	function convertImageToBase64($path=0, $image=0){
		return '0';

		if( $path === 0 || $image === 0){
			return '0';
		}
		else{
			$fullpath= $path.$image;
			$type = pathinfo($fullpath, PATHINFO_EXTENSION);
			$data = @file_get_contents($fullpath);
			if($data === FALSE)	
				$base64 = 'data:image/png;base64,';
			else
				$base64 = 'data:image/' . $type . ';base64,' . base64_encode($data);
			
			return $base64;
		}
	}
}

if ( ! function_exists('convertImageToBase64ByAjax')) {
	function convertImageToBase64ByAjax($fullpath=0){
		if( $fullpath === 0){
			return '0';
		}
		else{
			$type = pathinfo($fullpath, PATHINFO_EXTENSION);
			$data = @file_get_contents($fullpath);
			if(!$data)	
				$base64 = 'data:image/png;base64,';
			else
				$base64 = 'data:image/' . $type . ';base64,' . base64_encode($data);
			
			return $base64;
		}
	}
}